import React, { useState } from 'react';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [source, setSource] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [urls, setUrls] = useState([]);
  const [showUrlInput, setShowUrlInput] = useState(false);
  const [numUrls, setNumUrls] = useState(0);
  const [currentUrlCount, setCurrentUrlCount] = useState(0);
  const [newUrl, setNewUrl] = useState('');
  const [isInitialized, setIsInitialized] = useState(false);

  // Handle URL input process
  const handleUrlInputStart = () => {
    setShowUrlInput(true);
  };

  const handleNumUrlsChange = (e) => {
    setNumUrls(parseInt(e.target.value) || 0);
    setCurrentUrlCount(0);
    setUrls([]);
  };

  const handleAddUrl = () => {
    if (newUrl) {
      setUrls([...urls, newUrl]);
      setNewUrl('');
      setCurrentUrlCount(currentUrlCount + 1);

      if (currentUrlCount + 1 >= numUrls) {
        // All URLs added, now initialize the backend
        initializeBackend([...urls, newUrl]);
      }
    }
  };

  // Initialize backend with URLs
  const initializeBackend = async (urlList) => {
    setLoading(true);
    try {
      const response = await fetch('/api/initialize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ urls: urlList }),
      });

      if (response.ok) {
        setIsInitialized(true);
        setShowUrlInput(false);
        setLoading(false);
      } else {
        console.error('Failed to initialize backend');
        setLoading(false);
      }
    } catch (error) {
      console.error('Error initializing backend:', error);
      setLoading(false);
    }
  };

  // Process query
  const handleSubmit = async () => {
    if (!query) return;

    setLoading(true);
    try {
      const response = await fetch('/api/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          query: query,
          source: source || '' 
        }),
      });

      const data = await response.json();
      setAnswer(data.answer + '\n\nSources: ' + data.sources);
      setLoading(false);
    } catch (error) {
      console.error('Error processing query:', error);
      setAnswer('An error occurred while processing your query.');
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* Left section for inputs */}
      <div className="input-section">
        {/* Siri-like wave animation */}
        <div className="wave-container">
          <div className="wave-circle">
            <div className="wave"></div>
            <div className="wave"></div>
            <div className="wave"></div>
            <div className="wave"></div>
            <div className="wave"></div>
            <div className="wave"></div>
          </div>
        </div>

        {/* Floating particles */}
        <div className="particles-container">
          <div className="particle"></div>
          <div className="particle"></div>
          <div className="particle"></div>
          <div className="particle"></div>
          <div className="particle"></div>
          <div className="particle"></div>
          <div className="particle"></div>
        </div>

        <div className="input-container">
          {!isInitialized && !showUrlInput ? (
            <>
              <h2 className="section-title">Initialize Research</h2>
              <p className="instruction-text">Start by adding URLs for your research data</p>
              <button className="submit-btn" onClick={handleUrlInputStart}>Add URLs</button>
            </>
          ) : showUrlInput ? (
            <>
              <h2 className="section-title">Add Research Sources</h2>

              {currentUrlCount === 0 && (
                <>
                  <label htmlFor="numUrls">How many URLs would you like to input?</label>
                  <input
                    type="number"
                    id="numUrls"
                    value={numUrls}
                    onChange={handleNumUrlsChange}
                    min="1"
                  />
                </>
              )}

              {numUrls > 0 && currentUrlCount < numUrls && (
                <>
                  <label htmlFor="newUrl">Enter URL {currentUrlCount + 1} of {numUrls}:</label>
                  <input
                    type="text"
                    id="newUrl"
                    value={newUrl}
                    onChange={(e) => setNewUrl(e.target.value)}
                    placeholder="https://example.com"
                  />
                  <button className="submit-btn" onClick={handleAddUrl}>Add URL</button>
                </>
              )}

              {urls.length > 0 && (
                <div className="urls-list">
                  <h3>Added URLs:</h3>
                  <ul>
                    {urls.map((url, index) => (
                      <li key={index}>{url}</li>
                    ))}
                  </ul>
                </div>
              )}

              {loading && <div className="loading">Initializing database... Please wait...</div>}
            </>
          ) : (
            <>
              <h2 className="section-title">Research Query</h2>

              <label htmlFor="query">Enter your query</label>
              <input 
                type="text" 
                id="query" 
                name="query" 
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="What would you like to research?"
              />

              <label htmlFor="source">Source (optional)</label>
              <input 
                type="text" 
                id="source" 
                name="source" 
                value={source}
                onChange={(e) => setSource(e.target.value)}
                placeholder="Add reference sources or constraints..."
              />

              <button 
                className="submit-btn" 
                onClick={handleSubmit}
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Process Query'}
              </button>
            </>
          )}
        </div>
      </div>

      {/* Right section for output */}
      <div className="output-section">
        <div className="output-container">
          <h2 className="section-title">Research Findings</h2>

          <label htmlFor="answer">Results</label>
          <textarea 
            id="answer" 
            name="answer" 
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
            placeholder="Your research results will appear here..."
            rows="12"
            readOnly
          ></textarea>
        </div>
      </div>
    </div>
  );
}

export default App;
